mapper.select:
select * from
(select * from
(select no, userid_w, userid_r, subject, content, to_char(writedate, 'MM-DD') writedate, message_check, message_save, spam from message where 


		userid_w=#{userid}  


		userid_r=#{userid} 



	and spam='Y'	


	and message_save='Y'	


	and message_save='N'


	and spam='N' and message_save='N'





				and userid_w


				and userid_r




		and ${searchKey}

	like #{searchWord} 

order by no desc) where
rownum<=${currentPageRecord}		
order by no asc) where
rownum<=${lastPageRecordCount}
order by no desc
