mapper.select:
	select member.username, member.userid, member.tel1, trunc(months_between(trunc(sysdate), birth)/12) birth, member.gender, teacher.desired_wage, teacher.cctv , teacher.pic, to_char(t_p_table.apply_date, 'YYYY-MM-DD HH:MI') apply_date
		from member, t_p_table, teacher 
    	where member.userid = t_p_table.t_userid and t_p_table.t_userid = teacher.userid and t_p_table.job_board_no = #{param1}

select count(t_userid) 
	from t_p_table
	where job_board_no = #{param1} and t_userid = #{param2}

mapper.update:
update parent_list set care_addr=#{rbVO.care_addr}, dong_addr=#{rbVO.dong_addr}, time_type=#{rbVO.time_type}, pw_activity=#{rbVO.pw_activity}, wish_wage=#{rbVO.wish_wage}, consultation=#{rbVO.consultation}, time_consultation=#{rbVO.time_consultation}
	, wish_age=#{rbVO.wish_age}, wish_gender=#{rbVO.wish_gender}, content=#{rbVO.content}, lat=#{rbVO.lat}, lng =#{rbVO.lng}
	where job_board_no = #{param1}

update add_child set(child_birth, child_cnt) = 
	(#{cVO.child_birth}, #{cVO.child_cnt})
	 where job_board_no = #{param1}

update jb_regular_date(yoil, start_time, end_time, start_date, end_date, week) =
	(#{rdVO.yoil}, #{rdVO.start_time}, #{rdVO.end_time}, #{rdVO.start_date}, #{rdVO.end_date}, #{rdVO.week})
	where  job_board_no = #{param1}

update jb_specific_date(select_date, start_time, end_time) = 
	(#{sdVO.select_date}, #{sdVO.start_time}, #{sdVO.end_time})
	where job_board_no = #{param1}
